import 'package:free_tube_player/app/common/common.dart';

class DefaultADConfig {
  static final defaultADConfig = _adConfig();
  static const _androidDebug =
      '';
  static const _iosDebug =
      '';
  static const _androidRelease =
      '';
  static const _iosRelease =
      '';

  static String _adConfig() {
    if (isDebug) {
      if (isAndroid) {
        return _androidDebug;
      }
      return _iosDebug;
    }
    if (isAndroid) {
      return _androidRelease;
    }
    return _iosRelease;
  }
}
